CREATE TABLE IF NOT EXISTS `országok` (
  `név` varchar(27) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `fõváros` varchar(19) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `földrajzi_hely` varchar(37) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `terület` decimal(11,2) DEFAULT NULL,
  `államforma` varchar(30) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `népesség` int(11) DEFAULT NULL,
  `fõváros_népesség` int(11) DEFAULT NULL,
  `autójel` varchar(3) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `country` varchar(31) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `capital` varchar(19) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `pénznem` varchar(20) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `pénzjel` varchar(3) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `váltópénz` varchar(18) COLLATE utf8_hungarian_ci DEFAULT NULL,
  `telefon` int(11) DEFAULT NULL,
  `gdp` int(11) DEFAULT NULL,
  `kat` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_hungarian_ci;

